```class CircularQueue {
	constructor(size) {
		this.queue = new Array(size);
		this.front = -1;
		this.rear = -1;
		this.size = size;
	}

	enqueue(element) {
		if ((this.rear + 1) % this.size === this.front) {
			return 'Queue is full';
		}
		if (this.front === -1) this.front = 0;
		this.rear = (this.rear + 1) % this.size;
		this.queue[this.rear] = element;
